import * as THREE from "three";

import fragment from "./shader/fragment.glsl";

import vertex from "./shader/vertex.glsl";

import * as dat from "dat.gui";

import { gsap } from "gsap";

import OrbitControls from 'orbit-controls-es6';

import * as CANNON from 'cannon';

console.log('Three:', THREE);

console.log('dat:', dat);

console.log('gsap:', gsap, 'timeline:', gsap.timeline());

console.log('Orbit Controls:', OrbitControls);

console.log('Cannonjs:', CANNON);

console.log('vertexShader:', vertex);

console.log('fragmentShader:', fragment);

export default class Sketch {

    constructor(selector) {

        this.configScene();

        this.initParameters();

        this.configRenderer();

        this.attachDOM(selector);

        this.setDimensions(window.innerWidth - 20, window.innerHeight - 20);

        this.configCamera();

        this.resize();

        this.configControls();

        this.setupPhysics();

        this.setupResize();

        this.resize();

        this.addObjects();

        this.render();
    }

    configScene() {

        this.scene = new THREE.Scene();
    }

    initParameters() {

        this.paused = false;

        this.lastTime = 0;

        this.time = 0;

        this.fixedTimestep = 1.0 / 60.0;

        this.maxSubSteps = 3;
    }

    configRenderer() {

        this.renderer = new THREE.WebGLRenderer();

        this.renderer.setPixelRatio(window.devicePixelRatio);

        this.renderer.setClearColor(0xeeeeee, 1);
    }

    attachDOM(selector) {

        this.container = document.getElementById(selector);

        this.container.appendChild(this.renderer.domElement);
    }

    setDimensions(w, h) {

        this.width = window.innerWidth - 20;

        this.height = window.innerHeight - 20;
    }

    configCamera() {

        this.camera = new THREE.PerspectiveCamera(
            70,
            this.width / this.height,
            0.001,
            1000
        );

        this.camera.position.set(0, -4, 4);
    }

    configControls() {
        this.controls = new OrbitControls(this.camera, this.renderer.domElement);
        this.controls.autoRotate = false;
        this.controls.enableZoom = true;
        this.controls.enablePan = true;
        this.controls.update();
    }

    configSettings() {

        let that = this;

        this.settings = {
            time: 0,
            amlitude: 1,
            diffAmplitude: 1,
            period1: 1,
            period2: 1,
            perlinAmplitude: 1,
            timespeed: 1,
            oneWave: 1000,
            size: 5,
            fly: function () {
                alert("fly");
            }
        };

        this.gui = new dat.GUI();

        this.gui.add(this.settings, "time", 0, 100, 0.01);

        this.gui.add(this.settings, "fly").name("Test fly");
    }

    setupResize() {

        window.addEventListener("resize", this.resize.bind(this));
    }

    resize() {

        this.setDimensions(window.innerWidth - 20, window.innerHeight - 20);

        this.renderer.setSize(this.width, this.height);

        this.camera.aspect = this.width / this.height;

        this.camera.updateProjectionMatrix();
    }

    setupPhysics() {

        this.world = new CANNON.World();

        this.world.gravity.set(0, 0, -9);

        this.world.broadphase = new CANNON.NaiveBroadphase();

        const radius = 1;

        this.sphereBody = new CANNON.Body({
            mass: 5,
            position: new CANNON.Vec3(0, 0, 10),
            shape: new CANNON.Sphere(radius)
        });

        this.world.addBody(this.sphereBody);

        this.groundBody = new CANNON.Body({
            mass: 0
        });

        this.groundShape = new CANNON.Plane();

        this.groundBody.addShape(this.groundShape);

        this.world.addBody(this.groundBody);
    }

    addObjects() {

        let that = this;

        const geometry = new THREE.SphereGeometry(1, 32, 32);

        const material = new THREE.MeshBasicMaterial({ color: 0xff0000 });

        this.sphere = new THREE.Mesh(geometry, material);

        this.scene.add(this.sphere);
    }

    stop() {

        this.paused = true;
    }

    play() {

        this.paused = false;
        this.render();
    }

    render() {

        if (this.paused) {

            return;
        }

        this.time += 0.05;

        if (this.lastTime !== undefined) {

            this.world.step(this.fixedTimestep);
        }

        this.lastTime = this.time;

        this.sphere.position.copy(this.sphereBody.position);

        requestAnimationFrame(this.render.bind(this));

        this.renderer.render(this.scene, this.camera);
    }
}

const sketch = new Sketch("container");
